package operadores;

public class Asignacion {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
//Operadores +=, -=
		
		int n1=10;
		//Sumarle 10 a n1
		
		
		n1+=10;//n1= n1+10
		//restarle 10 a n1
		n1-=10;////n1= n1-10
		//lo que no podemos hacer es n1+10, asi el valor se pierde
		//en todo caso seria n1=n1+10
		//lo mismo con los demas
		n1*=10;//n1= n1*10
		n1/=10;////n1= n1/10
		
				
	}

}
